import './Landing.css';
import { useState, useEffect } from 'react'
import Search from '../search/Search'
import Filter from '../filter/Filter'
import Locations from '../locations/Locations'
import { fetchBRsByLoc } from '../../apiCalls';

const Landing = ({ updateLocs, locs }) => {
    //Handle fetch of actual bathrooms here
    //use updateLocs in Landing
    const [lat, setLat] = useState(null)
    const [lon, setLon] = useState(null)
    const [ada, setAda] = useState(null)
    const [unisex, setUnisex] = useState(null)
    useEffect(() => {
        handleBRsByLoc(lat, lon, ada, unisex)
    }, [])

    async function handleBRsByLoc(lat, lon, ada, unisex ) {
        try {
            const locations = await fetchBRsByLoc(lat, lon, ada, unisex)
            updateLocs(locations)
        } catch(error) {
            throw error
        }
    }

    function setLatLonState(lat = null, lon = null) {
        setLat(lat)
        setLon(lon)
    }

    function setParamState(ada = null, unisex = null) {
        setAda(ada)
        setUnisex(ada)
    }

    return (
        <div className='Landing_wrapper'>
        {/* handle coordinates fetch in search
        use the return to fire the fetch in here to fetch bathrooms */}
            <Search setLatLonState={setLatLonState}/>
            <Filter setParamState={setParamState}/>
            <Locations locs={locs}/>
        </div>
    )
}

export default Landing
